sourcesContent:
import Razorpay from 'razorpay';
import Order  from '../models/order.model'; // Assuming you have an 'order' model

const razorpayInstance = new Razorpay({
  // key_id: process.env.RAZORPAY_KEY_ID, // Your Razorpay key_id
  // key_secret: process.env.RAZORPAY_KEY_SECRET, // Your Razorpay key_secret
});

// Create a Razorpay Order
export const createOrder = async (req, res) => {
  const { amount, currency, receipt } = req.body;

  try {
    const options = {
      amount: amount * 100, // Convert to paise
      currency: currency || 'INR', // Default currency is INR
      receipt: receipt || 'order_rcptid_11',
      payment_capture: 1, // Auto capture
    };

    razorpayInstance.orders.create(options, async (err, order) => {
      if (err) {
        console.error('Razorpay Order creation failed', err);
        return res.status(500).json({ error: 'Failed to create order' });
      }

      // Save order details to your database (optional)
      const newOrder = new Order({
        order_id: order.id,
        amount,
        currency,
        receipt,
      });
      await newOrder.save();

      res.status(200).json({ 
        success: true, 
        order_id: order.id, 
        amount: amount * 100, 
        currency: currency,
        order
      });
    });
  } catch (error) {
    console.error('Error creating order:', error);
    res.status(500).json({ error: 'Internal Server Error' });
  }
};

// Verify Payment

export const verifyPayment = async (req, res) => {
  const { payment_id, order_id, signature } = req.body;
  const body = order_id + "|" + payment_id;

  const crypto = require('crypto');
  const generated_signature = crypto
    .createHmac('sha256', process.env.RAZORPAY_KEY_SECRET)
    .update(body)
    .digest('hex');

  if (generated_signature === signature) {
    try {
      // Payment signature is verified. You can mark order as paid in the DB here.
      const order = await Order.findOne({ order_id });
      if (!order) {
        return res.status(400).json({ error: 'Order not found' });
      }

      order.status = 'Paid';  // Update order status
      await order.save();

      res.status(200).json({ success: true, message: 'Payment Verified' });
    } catch (error) {
      console.error('Payment verification failed:', error);
      res.status(500).json({ error: 'Internal Server Error' });
    }
  } else {
    res.status(400).json({ error: 'Invalid signature' });
  }
};

