  },
  amount: {
    type: Number,
    required: true,
  },
  currency: {
    type: String,
    required: true,
  },
  receipt: {
    type: String,
    required: true,
  },
  status: {
    type: String,
    default: 'Pending',
  },
  created_at: {
    type: Date,
    default: Date.now,
  },
});

export default mongoose.model('Order', orderSchema);

